(()=>{document.addEventListener("DOMContentLoaded",()=>{let t=document.getElementById("themeToggle"),e=document.body;(localStorage.getItem("theme")||"light")==="dark"&&e.classList.add("dark-mode"),t.addEventListener("click",()=>{e.classList.contains("dark-mode")?(e.classList.remove("dark-mode"),localStorage.setItem("theme","light")):(e.classList.add("dark-mode"),localStorage.setItem("theme","dark"))})});})();
